import streamlit as st

변수 = st.session_state
if '체력' not in 변수:
    변수.체력=100
st.image('data:image/jpeg;base64,')
st.title(f"남은 체력: {변수.체력}")
if 변수.체력 < 0 :
    st.error("사망")

if st.button("네라 칼로 한대 스치기"):
    변수.체력 = 변수.체력 - 10
    st.rerun()

if st.button("빵 던지기"):
    변수.체력 = 변수.체력 + 10
    st.rerun()
